import React, { Component } from 'react'
import SideBar from "./SideBar"
import MiddleContainer from "./MiddleContainer"




 class MainPage extends Component {

    constructor(){
        super()
        this.state = {

            showForm: false,
            editForm: false,
            editAssign: null,
            isAssignements: false,
            showDetail: false,
            detailAssign: null,
            assignsNotClicked: true,
            showGrade:false

        }
    }

     clickAssigns = ()=>{

        this.setState({ isAssignements: !this.state.isAssignements})
        if(this.state.isAssignements){
            this.setState({showForm:false, editForm:false, showDetail: false, assignsNotClicked: false})
        }
    }

    handleClick = () => {
        this.setState({showForm: !this.state.showForm})
    }

    handleEdit = (assign) =>{
        this.setState({editAssign: assign, editForm: true})
    }

    displayAssigns = () =>{
        this.setState({showForm:false, editForm:false})

    }


    // handleBack = () => {

    //     this.setState({isAssignements: true})
    // }
    getAssign = (assign) =>{
        console.log("Attemt to show Detail ", assign)
            this.setState({ showDetail: true, detailAssign: assign })    
    }
    clickGrade =()=>{
        console.log("Attemt to see grade")
        showGrade({showGade})


    }


    render() {
        // console.log("klass Main page : ", this.props.klass)
        return this.props.klass? 

            <div id="tab">

                <SideBar   clickAssigns = {this.clickAssigns} clickGrade={this.clickGrade}/>

                <MiddleContainer klass={this.props.klass}
                                          currentUser = {this.props.currentUser}

                                       // sidbar props   
                                        showAssigs = {this.state.isAssignements}
                                        displayAssigns= {this.displayAssigns}
                                        // create props
                                        handleClick ={this.handleClick}
                                        showForm= {this.state.showForm}
                                         createAssign={this.props.createAssign} 
                                         assignments={this.props.assignments}
                                        //  edit props
                                        handleEdit= {this.handleEdit}
                                        editForm ={this.state.editForm}
                                        editAssign = {this.state.editAssign}
                                        editAssigns = {this.props.editAssigns}
                                        // delete props
                                        handleDelete = {this.props.handleDelete}
                                        // show props
                                          getAssign = {this.getAssign}
                                          showDetail = {this.state.showDetail}
                                          detailAssign = {this.state.detailAssign}


                                         />

            </div>



        : null
    }
}




export default MainPage
