import React, { Component } from 'react'
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class EditAssignment extends Component {
    constructor(props){
        super(props)
        this.state= {
            title: this.props.assign.title,
            startDate: this.props.assign.deadline,
            content: this.props.assign.content ? this.props.assign.content : ""
        }
    }

 handleTitle = (e) =>{
    this.setState({title: e.target.value})

}
handleDate = (date) =>{
    this.setState({startDate: date})

}
handleContent = (e) =>{
    console.log("Attempt to Edit content")
    this.setState({content: e.target.value})

}
EditAssign = (e)=> {
    e.preventDefault()
    console.log("Attempt to Edit Assign")
}


    render() {
        console.log("edit assing:", this.props)
        return (
            <div>
                Edit Form

                <form className="ui form" onSubmit = {this.EditAssign}>
                    <div className="field">
                        <label>Title</label>
                        <input type="text"  placeholder="Title"
                        name= "title"
                        value = {this.state.title}
                        onChange={this.handleTitle}
                        />
                    </div>
                    <div className="field">
                        <label>Due Date:</label>
                        <DatePicker 
                        selected={this.state.startDate}
                        onChange={(date ) => this.handleDate(date)}

                        minDate={new Date()}    
                    />
                </div>



                    <div className="field">
                        <label>Assignemt</label>
                        <textarea type="text"  placeholder="Content"
                        onChange= {this.handleContent}
                        value = {this.state.content}
                        name = "content"
                        ></textarea>
                    </div>
                    <div className="field">

                    </div>
                    <button className="ui button" type="submit">Submit</button>
                    </form>

            </div>
        )
    }
}

export default EditAssignment

