import React, { Component } from 'react'

class EditAssignment extends Component {
    constructor(){
        super()
        this.state= {
            title: "",
            date: new Date(),
            content: ""
        }
    }



    render() {
        return (
            <div>

<form className="ui form" onSubmit = {this.SubmitAssign}>
                    <div className="field">
                        <label>Title</label>
                        <input type="text"  placeholder="First Name"
                         name= "title"
                         value = {this.state.title}
                          onChange={this.handleChange}
                          />
                    </div>
                    <div className="field">
                        <label>Due Date:</label>
                        <DatePicker 
                        selected={this.state.startDate}
                        onChange={(date ) => this.handleDate(date)}

                        minDate={new Date()}    
                    />
                </div>



                    <div className="field">
                        <label>Assignemt</label>
                        <textarea type="text" name="con-name" placeholder="Last Name"
                        onChange= {this.handleContent}
                        value = {this.state.assign}
                        name = "assign"
                        ></textarea>
                    </div>
                    <div className="field">

                    </div>
                    <button className="ui button" type="submit">Submit</button>
                    </form>

            </div>
        )
    }
}

export default EditAssignment

// when clicked sent the action => render the form => get the info from the form 
// => make patch fetch request=> change the user's Assignments array with the most updated
