import React, { Component } from 'react';
import './SideItems.css'

// import EditAssignment from '../Components/EditAssignment'
import GradeContainer from '../Components/GradeContainer'
import Syllabus from '../Components/Syllabus'
import ShowAssignments from '../Components/ShowAssingments'

export default class MainPage2 extends Component {
    constructor(props){
        super(props)
        this.state= {
            grades: this.props.klass.student_assignments,
            currentSection: "MyAssignments",
            showDetail: false,
            detailAssign: null

        }
    }

    getAssign = (assign) =>{

        this.setState({ showDetail: true, detailAssign: assign })    
    }
    setCurrentSection =(section) =>{

        this.setState({currentSection: section})
    }
    handleBack=() =>{
        console.log("Attempt to go back")
        this.setState({ showDetail:false})

    }

    render() {
        // console.log("Main page klas: ", this.props.assignments)
        //let {assignments, klass, currentUser, editAssigns, createAssign, handleDelete } = this.props
        // const filtered = assignments.filter(a => a.klass_id === klass.id)
        return (

            <div id="tab">
               <div id="left-side">
                    <div onClick={()=> this.setCurrentSection('myDishes')}>My Dishes</div>
                    <div onClick={()=> this.setCurrentSection('eventRequests')}>Event Requests</div>
                    <div onClick={()=> this.setCurrentSection('calendar')}>Calendar</div>
                </div>
                <div id="right-side">
                    <div id="list">

                        {/* <DishesTable/> */}
                        {this.state.currentSection === 'myDishes'? 
                            <DishesTable 
                                user={user}
                                dishes={dishes} 
                                onDishDelete={this.props.onDishDelete}/> : null}
                        {/* <EventsTable /> */}
                        {this.state.currentSection === 'eventRequests'? <EventsTable events={events}/> : null}
                        {/* <EventsTable /> */}
                        {this.state.currentSection === 'calendar'? <DishesTable dishes={dishes} /> : null}

                    </div>
                </div>
            </div>

        )


    }
}
