import React, { Component } from 'react'
import { Modal, Button } from 'semantic-ui-react';


export default class CreateAssign extends Component {
    render() {
        return (

            <Modal size="mini" trigger={<Button className="ui green button" >Create New Dish +</Button>}>
                <Modal.Header>Create New Assignment</Modal.Header>
                    <Modal.Content >
                       <form class="ui form" onSubmit={this.handleSubmit}>
                            <div class="field">
                                <label>Name</label>
                                <input type="text" name="name" placeholder="Assignment Name" 
                                    value={this.state.name}
                                    onChange={this.handleInputChange}
                                />
                            </div>
                            <div class="field">
                                <label>Cuisine</label>
                                <input type="text" name="cuisine" placeholder="Assignment Cuisine"
                                    value={this.state.cuisine}
                                    onChange={this.handleInputChange}
                                />
                            </div>
                            <div class="field">
                                <label>Description</label>
                                <textarea rows="3" name="description" placeholder="Assignment Description"
                                    value={this.state.description}
                                    onChange={this.handleInputChange}
                                ></textarea>
                            </div>
                            <div class="field">
                                <label>Assignment Image</label>
                                <input type="text" name="img" placeholder="Assignment Cuisine"
                                    value={this.state.img}
                                    onChange={this.handleInputChange}
                                />
                            </div>
                            <button class="ui button" type="submit">Submit</button>
                        </form>
                   </Modal.Content>
            </Modal>

        )
    }
}
