import React, { Component } from 'react';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export default class AssignmentFrom extends Component {
    constructor(){
        super()
        this.state={
            startDate: new Date(),
            title: "",
            assign: ""

        }
    }

    handleChange = (e) =>{
        this.setState( {[e.target.name]:  e.target.value } )
    }
    handleDate = date => {
        this.setState ({startDate: date})

    }




getMonthFromString = (mon) => {
        return new Date(Date.parse(mon +" 1, 2012")).getMonth()+1
     }

    SubmitAssign = (e) => {
        e.preventDefault()

        console.log(this.state.startDate)

       const assign= { 
           title: this.state.title,
           content: this.state.assign
        //    deadline: this.state.

        }



    }

    render() {
        return (
            <div>
                <form class="ui form" onSubmit = {this.SubmitAssign}>
                    <div class="field">
                        <label>Title</label>
                        <input type="text"  placeholder="First Name"
                         name= "title"
                         value = {this.state.title}
                          onChange={this.handleChange}
                          />
                    </div>
                    <div class="field">
                        <label>Due Date:</label>
                        <DatePicker 
                        selected={this.state.startDate}
                        onChange={(date ) => this.handleDate(date)}

                        minDate={new Date()}    
                    />
                </div>



                    <div class="field">
                        <label>Assignemt</label>
                        <textarea type="text" name="last-name" placeholder="Last Name"
                        onChange= {this.handleChange}
                        value = {this.state.assign}
                        name = "assign"
                        ></textarea>
                    </div>
                    <div class="field">

                    </div>
                    <button class="ui button" type="submit">Submit</button>
                    </form>

            </div>
        )
    }
}
