        // console.log("filterd: ", filtered)
        return (
            <div>
                Grade
                {/* <Sort  klass={this.props.klass}  assignments= {filtered} />
                <Search /> */}

            <br/>
                <button className="fluid ui blue basic button">Student Table or Assign Table</button>
                <br/>

                {/* <AssignTable/> */}
