import React, { Component } from 'react'
import Assignment from './Assignment'

class Assignments extends Component {

//    klassAssigns (){
//        return this.props.assignments.filter(a => a.klass_id === this.props.klass.id)

//    }


    render() {
        console.log("Assignemts props ", this.props.assign)
        return (
            <div>

                <div className="ui relaxed divided list">
                    {     this.props.assignments.filter(a => a.klass_id === this.props.klass.id)
                            ().map(a =>  
                        a.klass_id === this.props.klass.id ?  
                        <Assignment key={a.id} assign={a} 
                            handleEdit={this.props.handleEdit}
                            handleDelete= {this.props.handleDelete}
                            />: null
                    ) }

                </div>



            </div>
        )
    }
}

export default Assignments
