              return < MainPage klass= {klass}/>

          } }/>

                <div className= "ui link centered cards">
                    {this.props.fetchedKlasses.map(k =>  
                    <ClassCard key={k.id} klass = {k} /> )}

                </div> 


            </div>
        )
    }
}


// accepts state and returns an object that props map to the state
