import React, { Component } from 'react';
// import logo from './logo.svg';
// , Redirect
import './App.css';
import  NavBar from './Containers/NavBar'
import { BrowserRouter, Route, Switch} from 'react-router-dom';
import HomePage from './Containers/HomePage';
import LoginForm from './Containers/LoginForm';
import MainPage from './Containers/MainPage';
import About from './Containers/About';
import {connect} from 'react-redux';


const url = "http://localhost:3001/klasses"
class App extends Component {

  componentDidMount(){

    fetch(url)
    .then(resp => resp.json())
    .then(klasses => {
        // console.log(klasses)
        // normally in react this.setState{ classes }
        // but now we don't want our component to handle state
        // must be invoked
        this.props.fetchedKlasses(klasses)
    } )
}
render(){
  return (

    <div className="App">
      <BrowserRouter>
        <NavBar/>
        <Switch>

        <Route exact path = "/" component = {About}/>

          <Route exact path = "/login" render= {() => (<LoginForm/>)}/>

          <Route exact path = "/homepage" render= {() => (<HomePage/>)}/>
          <Route exact path = "/mainpage" render= {() => (<MainPage/>)}/>
          <Route render= {() => <div > Page Not Found 404</div>}/>

        </Switch>    

      </BrowserRouter> 




    </div>
  );

}


}
// on Load of this page, dispatch an action that is FETCHED_KLASSES
const mapDispatchToProps = (dispatch) => {
  return{

      // props: () => {dispatch({type: "FETCHED_KLASSES"})}
    fetchedKlasses: (klasses) => { dispatch ( {type: "FETCHED_KLASSES", payload: klasses})}
  }

}
export default App;
